CREATE TABLE IF NOT EXISTS category(id INTEGER PRIMARY KEY AUTOINCREMENT,name VARCHAR(100),icon VARCHAR(50));
CREATE TABLE IF NOT EXISTS location(id INTEGER PRIMARY KEY AUTOINCREMENT,name VARCHAR(100),color VARCHAR(50));
CREATE TABLE IF NOT EXISTS stuff(id INTEGER PRIMARY KEY AUTOINCREMENT,name VARCHAR(100), categoryId INTEGER, locationId INTEGER, filepath TEXT NULL, desc VARCHAR(500) NULL, expiryDate VARCHAR(50) NULL);